create database universe;

\c universe

create table clusters(clusters_id serial primary key);
create table galaxy(galaxy_id serial primary key);
create table star(star_id serial primary key);
create table planet(planet_id serial primary key);
create table moon(moon_id serial primary key);

alter table clusters add unique(clusters_id);
alter table galaxy add unique(galaxy_id);
alter table star add unique(star_id);
alter table planet add unique(planet_id);
alter table moon add unique(moon_id);

alter table galaxy add column clusters_id int;
alter table star add column galaxy_id int;
alter table planet add column star_id int;
alter table moon add column planet_id int;

alter table galaxy add foreign key(clusters_id) references clusters(clusters_id);
alter table star add foreign key(galaxy_id) references galaxy(galaxy_id);
alter table planet add foreign key(star_id) references star(star_id);
alter table moon add foreign key(planet_id) references planet(planet_id);

alter table clusters add column name varchar(30), add column distance_mly int, add column recession_velocity_kms int not null, add column description varchar(200);
alter table galaxy add column name varchar(30), add column magnitude numeric, add column diameter_kly int not null, add column angular_size numeric;
alter table star add column name varchar(30), add column magnitude numeric, add column constelation varchar(50), add column description varchar(200);
alter table planet add column name varchar(30), add column is_inhabitable boolean, add column has_rings boolean, add column radius_km integer, add column description varchar(200);
alter table moon add column name varchar(30), add column orbital_speed int, add column discovered_by varchar(50), add column discovery_year int;

